mapper.select:
/* getCentroidSdo */
SELECT ST_AsText(ST_Centroid(A.geom))
FROM (
	SELECT geom 
	FROM sk_sdo 
	WHERE name = #{name}
	ORDER BY bjcd LIMIT 1
) A

/* getCentroidSgg */
SELECT ST_AsText(ST_Centroid(A.geom))
FROM (
	SELECT geom 
	FROM sk_sgg 
	WHERE bjcd LIKE #{bjcd} ||'%'
		AND name = #{name}
	ORDER BY bjcd LIMIT 1
) A

/* getCentroidEmd */
SELECT ST_AsText(ST_Centroid(A.geom))
FROM (
	SELECT geom 
	FROM sk_emd 
	WHERE bjcd LIKE #{bjcd} ||'%'
		AND name = #{name}
	ORDER BY bjcd LIMIT 1
) A

/* getListPlaceName */
SELECT gid, name_ko, ST_X(geom) as longitude, ST_Y(geom) as latitude, ST_AsGeoJSON(geom)
FROM place_name
WHERE name_ko LIKE '%' || #{search_value} || '%'
ORDER BY name_ko ASC 
OFFSET #{offset} LIMIT #{limit}
